    $( document ).ready(function() {

        $('.play-button').click(function () {
            $(this).fadeOut();
            $('#preview').fadeOut();
            $('#video').removeClass("d-none");
        });

        $('.close-overlay').click(function () {
          $(this).parent('.stories-overlay').children('.preview').children('#preview').fadeIn(); 
          $(this).parent('.stories-overlay').children('.preview').children('.play-button').fadeIn();          
          $(this).parent('.stories-overlay').css("display", "none");
        });

        $('.stasti').children('.container').children('.row').children('.col-6').click(function () {
          console.log($(this).children('.block-text').text());
          $('#item-text').text($(this).children('.block-text').text());
          $('.stories-overlay').children('.preview').children('#preview').attr("src", $(this).children('#story-img-data').attr("src"));
          $('.stories-overlay').children('.preview').children('.story-video').children('#story-video').attr("src", $(this).children('#story-video-data').attr("src"));
          $(this).parent('.row').parent('.container').parent('.stasti').children('.stories-overlay').css("display", "");
        });
});